var buttonBuy = document.querySelectorAll(".button-buy"), notifyPopup = document.querySelector(".modal-notify"), modalClose = notifyPopup.querySelector(".modal-close"); for (var buttonsBuy of buttonBuy) buttonsBuy.addEventListener("click", function (a) { a.preventDefault(), notifyPopup.classList.add("modal-show") }); modalClose.addEventListener("click", function (a) { a.preventDefault(), notifyPopup.classList.remove("modal-show") }), window.addEventListener("keydown", function (a) { 27 === a.keyCode && notifyPopup.classList.contains("modal-show") && (a.preventDefault(), notifyPopup.classList.remove("modal-show")) });